import React from "react";
import { Helmet } from "react-helmet";
import { Button, CheckBox, Input, Heading } from "../../components";
import { useNavigate } from "react-router-dom";
import ".."

export default function LOGINPage() {
  const navigate = useNavigate(); // Initialize useNavigate hook

  const handleLogin = () => {
    // Navigate to the home page upon login
    navigate("/home1");
  };

  return (
    <>
      <Helmet>
        <title>Attendance & tracking monitoring system</title>
        <meta name="description" content="Web site created using create-react-app" />
      </Helmet>
      <div className="container">
        <div className="login-form">
          <Heading size="xl" className="welcome-text">
            Welcome!
          </Heading>
          <Heading size="xl" className="login-heading">
            Login
          </Heading>
          <div className="form-container">
            <form>
              <h1 className="login-title">Login</h1>
              <div className="input-group">
                <Input shape="round" type="text" name="userName" placeholder="Username" />
                <Input shape="round" type="password" name="password" placeholder="Password" />
                <div className="remember-me">
                  <CheckBox shape="square" name="rememberme" label=" Remember me" id="rememberme" />
                </div>
              </div>
              <Button onClick={handleLogin} type="button" color="primary" size="sm" className="login-button">
                Login
              </Button>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
