import React from "react";
import { Helmet } from "react-helmet";
import { Text, Img, Button, Heading } from "../../components";
import { MenuItem, Menu, Sidebar } from "react-pro-sidebar";

export default function Home1Page() {
  const [collapsed, setCollapsed] = React.useState(false);

  const handleMenuClick = (path) => {
    // Define a function to handle menu item clicks
    window.location.href = path; // Redirect to the specified path
  };

  return (
    <>
      <Helmet>
        <title>Attendance & tracking monitoring system</title>
        <meta name="description" content="Web site created using create-react-app" />
      </Helmet>
      <div className="flex md:flex-col items-start w-full gap-4 bg-white-A700_01">
        <Sidebar
          width="288px !important"
          collapsedWidth="80px !important"
          collapsed={collapsed}
          onClick={() => {
            setCollapsed(!collapsed);
          }}
          className="flex flex-col h-screen pb-[65px] top-0 md:pb-5 border-black-900 border border-solid bg-indigo-800_01 shadow-xl !sticky overflow-auto"
        >
          <div className="flex flex-col self-stretch items-start">
            <Heading size="lg" as="h2" className="ml-[11px] !text-white-A700_01 !font-segoeui">
              Smart Access Control System{" "}
            </Heading>
            <Menu
              menuItemStyles={{
                button: {
                  padding: "7px",
                  gap: "5px",
                  alignSelf: "start",
                  color: "#ffffff",
                  fontWeight: 600,
                  fontSize: "24px",
                },
              }}
              rootStyles={{ ["&>ul"]: { gap: "40.50px" } }}
              className="flex flex-col self-center items-center w-full mt-[60px]"
            >
              <MenuItem onClick={() => handleMenuClick("/home")}>
                Home
              </MenuItem>
              <MenuItem onClick={() => handleMenuClick("/monitoring-ap")}>
                Monitoring AP
              </MenuItem>
              <MenuItem onClick={() => handleMenuClick("/new-reservation")}>
                New Reservation
              </MenuItem>
              <MenuItem onClick={() => handleMenuClick("/user-management")}>
                User Management
              </MenuItem>
            </Menu>
            <div className="flex items-center w-[82%] md:w-full mt-[153px] ml-7 gap-3">
              <div className="flex flex-col items-center justify-center w-[22%] p-2 bg-white-A700_01 rounded-[50%]">
                <Img src="images/img_user_fill.svg" alt="userfill_one" className="h-[24px] w-[24px] mb-1" />
              </div>
              <Text as="p" className="self-end !text-white-A700_01 !font-roboto !font-normal">
                Administration
              </Text>
            </div>
            <Button
              color="blue_gray_900"
              size="lg"
              shape="square"
              leftIcon={<Img src="images/img_signoutsqure.svg" alt="Sign_out_squre" />}
              className="self-center mt-[27px] gap-[11px] sm:px-5 font-roboto min-w-[214px]"
            >
              Logout
            </Button>
          </div>
        </Sidebar>
        {/* Rest of your code remains unchanged */}
      </div>
    </>
  );
}
